const cfg = {
  matrixBaseUrl: 'http://10.10.110.10:8008',
  user: 'ralf',
  password: 'REPLACE_WITH_RALF_PASSWORD',
  command: '!ping',
  replyText: '!pong',
  pollTimeoutMs: 1,
  allowedRoomIds: [], // leer = alle beigetretenen Raeume
};

async function jfetch(url, options = {}) {
  const res = await fetch(url, options);
  let body = null;
  try { body = await res.json(); } catch { body = {}; }
  return { res, body };
}

const staticData = getWorkflowStaticData('global');

const login = await jfetch(`${cfg.matrixBaseUrl}/_matrix/client/v3/login`, {
  method: 'POST',
  headers: { 'Content-Type': 'application/json' },
  body: JSON.stringify({
    type: 'm.login.password',
    identifier: { type: 'm.id.user', user: cfg.user },
    password: cfg.password,
  }),
});
if (!login.res.ok || !login.body.access_token) {
  throw new Error(`Matrix login failed: ${JSON.stringify(login.body)}`);
}

const token = login.body.access_token;
const ownUserId = login.body.user_id || `@${cfg.user}:otta.zone`;

const qs = new URLSearchParams({ timeout: String(cfg.pollTimeoutMs) });
if (staticData.nextBatch) qs.set('since', staticData.nextBatch);
const sync = await jfetch(`${cfg.matrixBaseUrl}/_matrix/client/v3/sync?${qs.toString()}`, {
  headers: { Authorization: `Bearer ${token}` },
});
if (!sync.res.ok) {
  throw new Error(`Matrix sync failed: ${JSON.stringify(sync.body)}`);
}

const nextBatch = sync.body.next_batch;
const joined = sync.body.rooms?.join || {};
const roomIds = Object.keys(joined);

if (!staticData.initialized) {
  staticData.initialized = true;
  if (nextBatch) staticData.nextBatch = nextBatch;
  return [{ json: {
    status: 'initialized',
    joinedRooms: roomIds,
    roomCount: roomIds.length,
    note: 'Erster Lauf setzt nur den Sync-Checkpoint. Ab dem n√§chsten Poll wird auf neue !ping reagiert.'
  } }];
}

const allowedSet = new Set((cfg.allowedRoomIds || []).filter(Boolean));
const replies = [];
let checkedEvents = 0;

for (const [roomId, roomData] of Object.entries(joined)) {
  if (allowedSet.size && !allowedSet.has(roomId)) continue;
  const events = roomData?.timeline?.events || [];
  checkedEvents += events.length;

  for (const ev of events) {
    if (ev?.type !== 'm.room.message') continue;
    if (ev?.sender !== ownUserId) continue;
    if (ev?.content?.body !== cfg.command) continue;

    const txnId = `${Date.now()}-${Math.floor(Math.random() * 100000)}-${replies.length}`;
    const send = await jfetch(`${cfg.matrixBaseUrl}/_matrix/client/v3/rooms/${encodeURIComponent(roomId)}/send/m.room.message/${txnId}`, {
      method: 'PUT',
      headers: {
        Authorization: `Bearer ${token}`,
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        msgtype: 'm.text',
        body: cfg.replyText,
        'm.relates_to': {
          'm.in_reply_to': { event_id: ev.event_id }
        }
      }),
    });

    if (!send.res.ok) {
      throw new Error(`Matrix send failed in ${roomId}: ${JSON.stringify(send.body)}`);
    }

    replies.push({ roomId, triggerEventId: ev.event_id, sendResponse: send.body });
  }
}

if (nextBatch) staticData.nextBatch = nextBatch;

if (!replies.length) {
  return [{ json: {
    status: 'no_ping',
    roomCount: roomIds.length,
    joinedRooms: roomIds,
    checkedEvents,
    filteredRoomCount: allowedSet.size || roomIds.length
  } }];
}

return [{ json: {
  status: 'sent',
  repliesSent: replies.length,
  replies,
  roomCount: roomIds.length,
  checkedEvents,
  note: 'Pollt Matrix-Raeume und antwortet auf !ping mit !pong.'
} }];
