const cfg = {
  matrixBaseUrl: 'http://10.10.110.10:8008',
  roomId: '!zgNJyJDjjaRfUYAUQZ:otta.zone',
  user: 'ralf',
  password: 'REPLACE_WITH_RALF_PASSWORD',
};

async function jfetch(url, options = {}) {
  const res = await fetch(url, options);
  let body = null;
  try { body = await res.json(); } catch { body = {}; }
  return { res, body };
}

const staticData = getWorkflowStaticData('global');

const login = await jfetch(`${cfg.matrixBaseUrl}/_matrix/client/v3/login`, {
  method: 'POST',
  headers: { 'Content-Type': 'application/json' },
  body: JSON.stringify({
    type: 'm.login.password',
    identifier: { type: 'm.id.user', user: cfg.user },
    password: cfg.password,
  }),
});
if (!login.res.ok || !login.body.access_token) {
  throw new Error(`Matrix login failed: ${JSON.stringify(login.body)}`);
}
const token = login.body.access_token;

const qs = new URLSearchParams({ timeout: '1' });
if (staticData.nextBatch) qs.set('since', staticData.nextBatch);
const sync = await jfetch(`${cfg.matrixBaseUrl}/_matrix/client/v3/sync?${qs.toString()}`, {
  headers: { Authorization: `Bearer ${token}` },
});
if (!sync.res.ok) {
  throw new Error(`Matrix sync failed: ${JSON.stringify(sync.body)}`);
}
if (sync.body.next_batch) staticData.nextBatch = sync.body.next_batch;

const room = sync.body.rooms?.join?.[cfg.roomId];
const events = room?.timeline?.events || [];
const pings = events.filter((e) => e.type === 'm.room.message' && e.content?.body === '!ping');
if (!pings.length) {
  return [{ json: { status: 'no_ping', roomId: cfg.roomId, checkedEvents: events.length } }];
}

const results = [];
for (const ev of pings) {
  const eventId = `${Date.now()}-${Math.floor(Math.random() * 100000)}`;
  const send = await jfetch(`${cfg.matrixBaseUrl}/_matrix/client/v3/rooms/${encodeURIComponent(cfg.roomId)}/send/m.room.message/${eventId}`, {
    method: 'PUT',
    headers: {
      Authorization: `Bearer ${token}`,
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({
      msgtype: 'm.text',
      body: '!pong',
      'm.relates_to': {
        'm.in_reply_to': { event_id: ev.event_id }
      }
    }),
  });

  if (!send.res.ok) {
    throw new Error(`Matrix send failed: ${JSON.stringify(send.body)}`);
  }

  results.push({ triggerEventId: ev.event_id, sendResponse: send.body });
}

return [{ json: {
  status: 'sent',
  roomId: cfg.roomId,
  repliesSent: results.length,
  replies: results,
  note: 'Antwortet auf !ping mit !pong im gepollten Raum.'
} }];
