const cfg = {
  matrixBaseUrl: 'http://10.10.110.10:8008',
  user: 'ralf',
  password: 'REPLACE_WITH_RALF_PASSWORD',
  accessToken: 'REPLACE_WITH_RALF_ACCESS_TOKEN',
  command: '!ping',
  replyText: '!pong',
  maxEventAgeMs: 120000,
  allowedRoomIds: [],
  allowedSenders: [],
};

async function jfetch(url, options = {}) {
  const method = options.method || 'GET';
  const headers = options.headers || {};
  const body = options.body;
  try {
    const resp = await this.helpers.httpRequest({
      method,
      url,
      headers,
      body,
      json: true,
      returnFullResponse: true,
      ignoreHttpStatusErrors: true,
    });
    const statusCode = Number(resp?.statusCode || 0);
    return { res: { ok: statusCode >= 200 && statusCode < 300, status: statusCode }, body: resp?.body || {} };
  } catch (err) {
    const statusCode = Number(err?.statusCode || 0);
    return { res: { ok: false, status: statusCode }, body: err?.response?.body || { error: String(err?.message || err) } };
  }
}

let token = cfg.accessToken;
if (!token || token.startsWith('REPLACE_')) {
  const login = await jfetch(`${cfg.matrixBaseUrl}/_matrix/client/v3/login`, {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify({
      type: 'm.login.password',
      identifier: { type: 'm.id.user', user: cfg.user },
      password: cfg.password,
    }),
  });
  if (!login.res.ok || !login.body.access_token) {
    throw new Error(`Matrix login failed: ${JSON.stringify(login.body)}`);
  }
  token = login.body.access_token;
}

const whoami = await jfetch(`${cfg.matrixBaseUrl}/_matrix/client/v3/account/whoami`, {
  headers: { Authorization: `Bearer ${token}` },
});
if (!whoami.res.ok || !whoami.body?.user_id) {
  throw new Error(`Matrix whoami failed: ${JSON.stringify(whoami.body)}`);
}
const ownUserId = whoami.body.user_id;

const joinedResp = await jfetch(`${cfg.matrixBaseUrl}/_matrix/client/v3/joined_rooms`, {
  headers: { Authorization: `Bearer ${token}` },
});
if (!joinedResp.res.ok) {
  throw new Error(`Matrix joined_rooms failed: ${JSON.stringify(joinedResp.body)}`);
}

const joinedRooms = joinedResp.body.joined_rooms || [];
const allowedSet = new Set((cfg.allowedRoomIds || []).filter(Boolean));
const replies = [];

for (const roomId of joinedRooms) {
  if (allowedSet.size && !allowedSet.has(roomId)) continue;

  const msgResp = await jfetch(`${cfg.matrixBaseUrl}/_matrix/client/v3/rooms/${encodeURIComponent(roomId)}/messages?dir=b&limit=30`, {
    headers: { Authorization: `Bearer ${token}` },
  });
  if (!msgResp.res.ok) continue;

  const events = (msgResp.body?.chunk || []).filter((ev) => ev?.type === 'm.room.message');
  const nowMs = Date.now();

  const pingEvent = events.find((ev) => {
    if (ev?.sender === ownUserId) return false;
    if (cfg.allowedSenders.length && !cfg.allowedSenders.includes(ev?.sender || '')) return false;
    if (ev?.content?.body !== cfg.command) return false;
    const eventTs = Number(ev?.origin_server_ts || 0);
    if (!eventTs) return false;
    if ((nowMs - eventTs) > Number(cfg.maxEventAgeMs || 120000)) return false;
    return true;
  });

  if (!pingEvent) continue;

  const alreadyReplied = events.some((ev) => {
    if (ev?.sender !== ownUserId) return false;
    if (ev?.content?.body !== cfg.replyText) return false;
    return ev?.content?.['m.relates_to']?.['m.in_reply_to']?.event_id === pingEvent.event_id;
  });

  if (alreadyReplied) continue;

  const txnId = `${Date.now()}-${Math.floor(Math.random() * 100000)}-${replies.length}`;
  const send = await jfetch(`${cfg.matrixBaseUrl}/_matrix/client/v3/rooms/${encodeURIComponent(roomId)}/send/m.room.message/${txnId}`, {
    method: 'PUT',
    headers: {
      Authorization: `Bearer ${token}`,
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({
      msgtype: 'm.text',
      body: cfg.replyText,
      'm.relates_to': { 'm.in_reply_to': { event_id: pingEvent.event_id } },
    }),
  });

  if (!send.res.ok) {
    throw new Error(`Matrix send failed in ${roomId}: ${JSON.stringify(send.body)}`);
  }

  replies.push({ roomId, triggerEventId: pingEvent.event_id, sendResponse: send.body });
}

if (!replies.length) {
  return [{ json: { status: 'no_ping', joinedRooms: joinedRooms.length } }];
}

return [{ json: { status: 'sent', repliesSent: replies.length, replies } }];
