const cfg = {
  matrixBaseUrl: 'http://10.10.110.10:8008',
  user: 'ralf',
  password: 'REPLACE_WITH_RALF_PASSWORD',
  commandPrefix: '!ralf',
  pollTimeoutMs: 1,
  allowedRoomIds: [],
};

async function jfetch(url, options = {}) {
  const method = options.method || 'GET';
  const headers = options.headers || {};
  const body = options.body;

  try {
    const resp = await this.helpers.httpRequest({
      method,
      url,
      headers,
      body,
      json: true,
      returnFullResponse: true,
      ignoreHttpStatusErrors: true,
    });
    const statusCode = Number(resp?.statusCode || 0);
    return {
      res: { ok: statusCode >= 200 && statusCode < 300, status: statusCode },
      body: resp?.body || {},
    };
  } catch (err) {
    const statusCode = Number(err?.statusCode || 0);
    return {
      res: { ok: false, status: statusCode },
      body: err?.response?.body || { error: String(err?.message || err || 'http request failed') },
    };
  }
}

function answerFor(question) {
  const q = question.toLowerCase();
  if (q.includes('help')) {
    return 'Befehle: !ping, !ralf <frage>, !plan <ziel>';
  }
  if (q.includes('status') || q.includes('health')) {
    return 'RALF Status: Plattform laeuft. Nutze Dashy fuer Healthchecks.';
  }
  if (q.includes('semaphore')) {
    return 'Semaphore intern: https://semaphore.homelab.lan';
  }
  if (q.includes('synapse') || q.includes('matrix')) {
    return 'Matrix extern: https://matrix.otta.zone | intern: https://synapse.homelab.lan';
  }
  if (q.includes('gitea')) {
    return 'Gitea intern: https://gitea.homelab.lan';
  }
  return 'Ich habe die Frage verstanden, aber noch keine feste Antwortregel dafuer. Nutze !plan <ziel> fuer einen strukturierten Vorschlag.';
}

const staticData = $getWorkflowStaticData('global');
const login = await jfetch(`${cfg.matrixBaseUrl}/_matrix/client/v3/login`, {
  method: 'POST',
  headers: { 'Content-Type': 'application/json' },
  body: JSON.stringify({
    type: 'm.login.password',
    identifier: { type: 'm.id.user', user: cfg.user },
    password: cfg.password,
  }),
});
if (!login.res.ok || !login.body.access_token) throw new Error(`Matrix login failed: ${JSON.stringify(login.body)}`);

const token = login.body.access_token;
const ownUserId = login.body.user_id || `@${cfg.user}:otta.zone`;

let syncUrl = `${cfg.matrixBaseUrl}/_matrix/client/v3/sync?timeout=${encodeURIComponent(String(cfg.pollTimeoutMs))}`;
if (staticData.nextBatch) {
  syncUrl += `&since=${encodeURIComponent(String(staticData.nextBatch))}`;
}
const sync = await jfetch(syncUrl, {
  headers: { Authorization: `Bearer ${token}` },
});
if (!sync.res.ok) throw new Error(`Matrix sync failed: ${JSON.stringify(sync.body)}`);

const nextBatch = sync.body.next_batch;
const joined = sync.body.rooms?.join || {};
if (!staticData.initialized) {
  staticData.initialized = true;
  if (nextBatch) staticData.nextBatch = nextBatch;
  return [{ json: { status: 'initialized', roomCount: Object.keys(joined).length } }];
}

const allowedSet = new Set((cfg.allowedRoomIds || []).filter(Boolean));
const replies = [];
for (const [roomId, roomData] of Object.entries(joined)) {
  if (allowedSet.size && !allowedSet.has(roomId)) continue;
  const events = roomData?.timeline?.events || [];
  for (const ev of events) {
    const body = ev?.content?.body || '';
    if (ev?.type !== 'm.room.message') continue;
    if (!body.startsWith(cfg.commandPrefix)) continue;
    if (ev?.sender === ownUserId) continue;

    const question = body.slice(cfg.commandPrefix.length).trim();
    const reply = answerFor(question);
    const txnId = `${Date.now()}-${Math.floor(Math.random() * 100000)}-${replies.length}`;

    const send = await jfetch(`${cfg.matrixBaseUrl}/_matrix/client/v3/rooms/${encodeURIComponent(roomId)}/send/m.room.message/${txnId}`, {
      method: 'PUT',
      headers: {
        Authorization: `Bearer ${token}`,
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        msgtype: 'm.text',
        body: reply,
        'm.relates_to': { 'm.in_reply_to': { event_id: ev.event_id } }
      }),
    });
    if (!send.res.ok) throw new Error(`Matrix send failed in ${roomId}: ${JSON.stringify(send.body)}`);
    replies.push({ roomId, triggerEventId: ev.event_id, reply });
  }
}

if (nextBatch) staticData.nextBatch = nextBatch;
if (!replies.length) return [{ json: { status: 'no_question' } }];
return [{ json: { status: 'sent', repliesSent: replies.length, replies } }];
